(function($){
	'use strict';
	$.fn.LineProgressbar=function(r){var r=$.extend({percentage:null,ShowProgressCount:!0,duration:1e3,fillBackgroundColor:"#3498db",backgroundColor:"#EEEEEE",radius:"0px",height:"10px",width:"100%"},r);return this.each(function(o,n){$(n).html('<div class="progressbar"><div class="proggress"></div><div class="percentCount"></div></div>');var d=$(n).find(".proggress"),i=$(n).find(".progressbar");d.css({backgroundColor:r.fillBackgroundColor,height:r.height,borderRadius:r.radius}),i.css({width:r.width,backgroundColor:r.backgroundColor,borderRadius:r.radius}),d.animate({width:r.percentage+"%"},{step:function(o){r.ShowProgressCount&&$(n).find(".percentCount").text(Math.round(o)+"%")},duration:r.duration})})};
})(jQuery);